// Given - there is internet connection
// When - the user successfully signs in to the app
/** Then - news cards are displayed in rows
 * and each card contains
 * an image, text and a view button
 * (cards can have one or more images
 * scrollable horizontally)
 */
logInPage.login();
cy.get('div[class=react-swipeable-view-container]', { timeout: 6000 }).its('length').then(newsCardsCount => {
    let newsCardsArray = [];
    newsCardsArray[newsCardsCount - 1] = newsCardsCount;
    let countNum = 0;
    cy.wrap(newsCardsArray).each((index) => {
        cy.get('#article_image_' + countNum)
            .should('have.attr', 'src')
            .and('exist');
        cy.get('#article_link_' + countNum)
            .should('include.text', 'View')
            .should('have.attr', 'href')
            .and('exist');
        countNum += 1;
        let newsCardsHeadings = ':nth-child(' + countNum + ') > .MuiPaper-root > .MuiCardContent-root > .MuiTypography-h5';
        let newsCardsBodies = ':nth-child(' + countNum + ') > .MuiPaper-root > .MuiCardContent-root > .MuiTypography-body1';
        cy.get(newsCardsHeadings).invoke('text').should('match', /^[a-zA-Z]*/).and('exist');
        cy.get(newsCardsBodies).should('include.text', 'This is a media card.').and('exist');
    });
});

// Given - there is no internet connection
// When - the user successfully signs in to the app
/** Then - “failed to load news” error message
 * and a Retry button are displayed
 */
logInPage.loadNewsFailed();
cy.get('#news_failed > .MuiTypography-gutterBottom')
    .should('contain', 'Failed to load news')
    .and('be.visible');
cy.get('.css-12wjmzy-MuiTypography-root')
    .should('contain', 'Failed to fetch')
    .and('be.visible');

logInPage.login();
// Given - the news cards are successfully loaded on the screen
cy.get('div[class=react-swipeable-view-container]', { timeout: 6000 })
    .its('length')
    .should('eq', 24);
cy.url().should('include', 'news');
// When - the user clicks on the view button of the card
// Then - user is navigated to the image of the card
cy.get('#article_link_0').invoke('attr', 'href').then(href => {
    cy.get('#article_link_0').click();
    cy.url().should('include', href);
});

// Given - the user opens web site for the first time (when not logged in yet)
// Then - login screen with user name and password entries and login button is displayed
cy.visit('http://localhost:3000');
cy.get('input[name=username]')
    .should('have.value', '')
    .and('be.visible');
cy.get('input[name=password]')
    .should('have.value', '')
    .and('be.visible');
cy.get('button[id=login_button]')
    .should('have.text', 'Sign In')
    .and('be.visible');

// Given - the user provided wrong user name and/or password
cy.visit('http://localhost:3000');
cy.get('input[name=username]')
    .should('be.visible').type('wrongusername');
cy.get('input[name=password]')
    .should('be.visible').type('wrongpassword');
// When - sign in button is clicked
cy.get('button[id=login_button]')
    .should('have.text', 'Sign In')
    .click();
// Then - error markers are displayed by user name and/or password entries
cy.get('p[id=username-helper-text]')
    .should('have.text', 'Wrong username')
    .and('be.visible');
cy.get('p[id=password-helper-text]')
    .should('have.text', 'Password incorrect')
    .and('be.visible');
cy.get('legend[class=css-1z7n62]')
    .should('have.length', 2);
cy.get('legend > span')
    .first()
    .should('have.text', 'Username');
cy.get('legend > span')
    .eq(1)
    .should('have.text', 'Password');

// Given - the user provided right user name and password
cy.visit('http://localhost:3000');
cy.get('input[name=username]').type('user1');
cy.get('input[name=password]').type('pa55word');
// When - sign in button is clicked
cy.get('button[id=login_button]').click();
// Then - user is taken to the news page
cy.url().should('include', 'news');
cy.get('.MuiToolbar-root > .MuiTypography-root')
    .should('contain', 'News');
cy.get('.MuiTypography-h2')
    .should('contain', 'Todays top news');
cy.get('.MuiContainer-root > .MuiTypography-h5')
    .and('contain', 'Some of the latest news articles for today');

logInPage.login();
// Given - the user opens web site next time (when previously logged in)
// Then - user is taken straight to the news screen
cy.url().should('include', 'news');
cy.get('.css-16ybtzv > .MuiTypography-h6')
    .and('contain', 'You\'re all caught up!');
